function sendNotification(selectedEvent, message, type) {
	const notificationDetails = {
		message,
		event: selectedEvent,
		notificationType: type,
	};
	if (stompClient) {
		stompClient.send(
			"/app/event-notification",
			{},
			JSON.stringify(notificationDetails)
		);
	}
}

export { Calendar, sendNotification };
