import styled from "styled-components";

const StyledButton = styled.button`
	cursor: pointer;
	display: block;
	padding: 1.5rem 3rem;
	border: none;
	background-color: #6c63ff;
	text-align: center;
	margin-top: 1rem;
	padding: 20px;
	border-radius: 10px;
	cursor: pointer;
	color: white;
	font-size: large;
	width: 100%;
`;

export default StyledButton;
